const products = [
    { 
        name: "Notebook ABC", 
        price: 4999.99, 
        image: 'data:image/jpeg;base64,'
    },
    { 
        name: "Fone de Ouvido Bluetooth", 
        price: 399.99, 
        image: 'data:image/jpeg;base64,'
    },
    { 
        name: "Placa de Vídeo rtx 4060", 
        price: 2000, 
        image:  'data:image/jpeg;base64,'
    },
    { 
        name: "Processador ryzen 9 9950x3d", 
        price: 5299, 
        image: "data:image/jpeg;base64," 
    }
];

const cart = [];

function renderProducts() {
    const productGrid = document.getElementById("product-grid");
    products.forEach(product => {
        const productCard = document.createElement("div");
        productCard.className = "product-card";
        productCard.innerHTML = `
            <img src="${product.image}" alt="${product.name}">
            <div class="info">
                <h3>${product.name}</h3>
                <p class="price">R$ ${product.price.toFixed(2)}</p>
                <button onclick="addToCart('${product.name}', ${product.price}, '${product.image}')">Comprar</button>
            </div>
        `;
        productGrid.appendChild(productCard);
    });
}

function addToCart(productName, productPrice, productImage) {
    const existingProduct = cart.find(item => item.name === productName);
    if (existingProduct) {
        existingProduct.quantity += 1;
    } else {
        cart.push({ name: productName, price: productPrice, image: productImage, quantity: 1 });
    }
    renderCart();
}

function removeFromCart(productName) {
    const productIndex = cart.findIndex(item => item.name === productName);
    if (productIndex > -1) {
        if (cart[productIndex].quantity > 1) {
            cart[productIndex].quantity -= 1;
        } else {
            cart.splice(productIndex, 1);
        }
    }
    renderCart();
}

function renderCart() {
    const cartItemsContainer = document.getElementById("cart-items");
    const cartTotalContainer = document.getElementById("cart-total");
    cartItemsContainer.innerHTML = "";

    if (cart.length === 0) {
        cartItemsContainer.innerHTML = '<p class="empty">Seu carrinho está vazio.</p>';
        cartTotalContainer.textContent = "Total: R$ 0,00";
        return;
    }

    let total = 0;

    cart.forEach(item => {
        total += item.price * item.quantity;
        const cartItem = document.createElement("div");
        cartItem.className = "cart-item";
        cartItem.innerHTML = `
            <img src="${item.image}" alt="${item.name}">
            <span>${item.name} (x${item.quantity})</span>
            <span class="price">R$ ${(item.price * item.quantity).toFixed(2)}</span>
            <div>
                <button onclick="removeFromCart('${item.name}')">-</button>
                <button onclick="addToCart('${item.name}', ${item.price}, '${item.image}')">+</button>
            </div>
        `;
        cartItemsContainer.appendChild(cartItem);
    });

    cartTotalContainer.textContent = `Total: R$ ${total.toFixed(2)}`;
}

renderProducts();
